// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract IotaHeroAccount {
    uint256 nextAccountId = 1;

    mapping(uint256 => address) public idToAddress;
    mapping(uint256 => string) public idToUsername;
    mapping(address => uint256) public addressToId;
    mapping(string => uint256) public usernameToId;

    function signUp(string memory _username)
        external
        usernameNotTaken(_username)
        noAccount
        usernameHasValidLength(_username)
    {
        addressToId[msg.sender] = nextAccountId;
        idToAddress[nextAccountId] = msg.sender;
        idToUsername[nextAccountId] = _username;
        usernameToId[_username] = nextAccountId;
        nextAccountId++;
    }

    modifier usernameNotTaken(string memory _username) {
        require(usernameToId[_username] == 0, "Username is taken");
        _;
    }
    modifier hasAccount() {
        require(isSignedUp() == true, "You don't have an account");
        _;
    }
    modifier noAccount() {
        require(addressToId[msg.sender] == 0, "You already have an account!");
        _;
    }
    modifier usernameHasValidLength(string memory _username) {
        uint256 length = utfStringLength(_username);
        require(
            length < 13 && length > 2,
            "Username must be between 3 and 12 characters"
        );
        _;
    }

    function getAddressForUsername(string memory _username)
        public
        view
        returns (address)
    {
        return idToAddress[usernameToId[_username]];
    }

    function usernameExists(string memory _username)
        public
        view
        returns (bool)
    {
        return (usernameToId[_username] != 0);
    }

    function isSignedUp() public view returns (bool) {
        return addressToId[tx.origin] != 0;
    }

    function getUsername() public view returns (string memory) {
        return idToUsername[addressToId[msg.sender]];
    }

    function getUsernameByAddress(address _address)
        public
        view
        returns (string memory)
    {
        return idToUsername[addressToId[_address]];
    }

    function getUsernameLength() public view returns (uint256) {
        string memory username = getUsername();
        return utfStringLength(username);
    }

    function currentUserCount() public view returns (uint256) {
        return (nextAccountId - 1);
    }

    function utfStringLength(string memory str)
        internal
        pure
        returns (uint256 length)
    {
        uint256 i = 0;
        bytes memory string_rep = bytes(str);

        while (i < string_rep.length) {
            if (string_rep[i] >> 7 == 0) i += 1;
            else if (string_rep[i] >> 5 == bytes1(uint8(0x6))) i += 2;
            else if (string_rep[i] >> 4 == bytes1(uint8(0xE))) i += 3;
            else if (string_rep[i] >> 3 == bytes1(uint8(0x1E)))
                i += 4;
                //For safety
            else i += 1;

            length++;
        }
    }
}
