// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
import "./IotaHeroFactory.sol";
import "./IotaHeroGameTokens.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";

contract IotaHeroAdventure is Ownable, ERC1155Receiver {
    IotaHeroFactory public heroToken;
    IotaHeroGameTokens public tokens;
    uint256 randomNonce = 0;
    uint256 adventureCounter = 0;
    uint256 adventureCoolDown = 30 seconds;

    mapping(uint256 => address) adventureToAccount;
    mapping(uint256 => uint256) adventureToHero;
    mapping(uint256 => bool) heroIsAdventuring;
    mapping(uint256 => bool) adventureToCompleted;
    mapping(uint256 => uint256) heroIsReadyToAdventureAt;
    mapping(uint256 => uint256[]) adventureToEquipment;

    modifier correctHeroForAdventure(uint256 _heroId, uint256 _adventureId) {
        require(
            adventureToHero[_adventureId] == _heroId,
            "This hero is not on this adventure"
        );
        _;
    }
    modifier adventureNotCompleted(uint256 _adventureId) {
        require(
            adventureToCompleted[_adventureId] == false,
            "Adventure already completed"
        );
        _;
    }

    function setHeroTokens(address tokensAddress) public onlyOwner {
        heroToken = IotaHeroFactory(tokensAddress);
    }

    function setTokens(address tokensAddress) public onlyOwner {
        tokens = IotaHeroGameTokens(tokensAddress);
    }

    function getTokenAddress() public view returns (address) {
        return address(tokens);
    }

    event AdventureInitiated(
        address player,
        uint256 heroId,
        uint256 adventureId
    );
    event AdventureCompleted(
        address indexed player,
        uint256 indexed heroId,
        uint256 adventureId
    );

    function completeAdventure(
        uint256 _heroId,
        uint256 adventureId,
        uint256[] calldata itemsToReturn,
        uint256[] calldata lootTokenIds,
        uint256[] calldata lootAmounts
    )
        external
        onlyOwner
        adventureNotCompleted(adventureId)
        correctHeroForAdventure(_heroId, adventureId)
    {
        address player = adventureToAccount[adventureId];
        adventureToCompleted[adventureId] = true;
        heroIsAdventuring[_heroId] = false;

        uint256[] memory arr = new uint256[](itemsToReturn.length);
        for (uint256 i = 0; i < itemsToReturn.length; i++) {
            arr[i] = 1;
        }
        // return equipment items from the adventure
        tokens.safeBatchTransferFrom(
            address(this),
            player,
            itemsToReturn,
            arr,
            ""
        );
        tokens.awardAdventurer(player, lootTokenIds, lootAmounts);
        emit AdventureCompleted(player, _heroId, adventureId);
    }

    function heroIsReadyToAdventure(uint256 _heroId)
        internal
        view
        returns (bool)
    {
        return (heroIsReadyToAdventureAt[_heroId] <= block.timestamp);
    }

    function isAdventureCompleted(uint256 _adventureId)
        public
        view
        returns (bool)
    {
        return adventureToCompleted[_adventureId];
    }

    function getEquipmentForAdventure(uint256 _adventureId)
        public
        view
        returns (uint256[] memory)
    {
        return adventureToEquipment[_adventureId];
    }

    function getHeroAdventureStatus(uint256 _heroId)
        external
        view
        returns (bool, uint256)
    {
        return (heroIsAdventuring[_heroId], heroIsReadyToAdventureAt[_heroId]);
    }

    function goAdventure(uint256 _heroId, uint256[] calldata _equipment)
        external
    {
        require(heroToken.isSignedUp() == true, "not signed up");
        require(msg.sender == heroToken.ownerOf(_heroId), "not hero owner");
        require(heroIsAdventuring[_heroId] == false, "already adventuring");
        require(heroIsReadyToAdventure(_heroId), "not ready");
        require(
            tokens.isApprovedForAll(msg.sender, address(this)),
            "You must approve first"
        );
        uint256[] memory arr = new uint256[](_equipment.length);
        for (uint256 i = 0; i < _equipment.length; i++) {
            arr[i] = 1;
        }
        tokens.safeBatchTransferFrom(
            msg.sender,
            address(this),
            _equipment,
            arr,
            ""
        );
        heroIsReadyToAdventureAt[_heroId] = uint32(
            block.timestamp + adventureCoolDown
        );
        adventureCounter++;
        adventureToEquipment[adventureCounter] = _equipment;
        adventureToAccount[adventureCounter] = msg.sender;
        adventureToHero[adventureCounter] = _heroId;
        adventureToCompleted[adventureCounter] = false;
        heroIsAdventuring[_heroId] = true;
        emit AdventureInitiated(msg.sender, _heroId, adventureCounter);
    }

    function onERC1155Received(
        address,
        address,
        uint256 tokenId,
        uint256 amount,
        bytes memory data
    ) public virtual override returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }
}
