// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";

import "./IotaHeroFactory.sol";

contract IotaHeroTrainingFacility is Ownable, ERC1155Receiver {
    IotaHeroFactory public heroToken;
    IotaHeroGameTokens public tokens;

    uint256 currencyTokenId;
    uint256 basePrice = 10;
    uint32 facilityCounter = 0;
    mapping(address => uint32) addressToFacilitiesCount;
    mapping(address => mapping(uint32 => uint8)) addressToFacilityLevel;
    mapping(address => uint256) addressUpgradeCooldown;
    mapping(uint256 => uint256) heroIsReadyToTrainAt;
    uint256 baseUpgradeCooldown = 60 seconds;
    uint256 trainingCooldown = 60 seconds;

    struct Facility {
        uint32 id;
        string name;
        uint256 consGain;
        uint256 strGain;
        uint256 agiGain;
        uint256 intGain;
    }

    Facility[] public facilities;

    modifier readyToUpgrade() {
        require(addressUpgradeCooldown[msg.sender] < block.timestamp);
        _;
    }

    function getAddressCanUpgradeAt(address upgrader)
        external
        view
        returns (uint256)
    {
        return addressUpgradeCooldown[upgrader];
    }

    function setHeroTokens(address tokensAddress) external onlyOwner {
        heroToken = IotaHeroFactory(tokensAddress);
    }

    function setGameTokens(address tokensAddress) external onlyOwner {
        tokens = IotaHeroGameTokens(tokensAddress);
    }

    function setCurrencyToken(uint256 tokenId) external onlyOwner {
        currencyTokenId = tokenId;
    }

    function setBasePrice(uint256 price) external onlyOwner {
        basePrice = price;
    }

    function getBasePrice() external view returns (uint256) {
        return basePrice;
    }

    function getUserFacilities()
        external
        view
        hasAccount
        returns (Facility[] memory)
    {
        uint32 c;
        Facility[] memory ownedFacilities = new Facility[](
            addressToFacilitiesCount[msg.sender]
        );
        for (uint32 i; i < facilities.length; i++) {
            if (addressToFacilityLevel[msg.sender][i] > 0) {
                ownedFacilities[c] = facilities[i];
                c++;
            }
        }
        return ownedFacilities;
    }

    function getFacilitiesWithUserLevels()
        external
        view
        hasAccount
        returns (Facility[] memory, uint32[] memory)
    {
        uint32[] memory levels = new uint32[](facilities.length);
        for (uint32 i; i < facilities.length; i++) {
            levels[i] = addressToFacilityLevel[msg.sender][i];
        }
        return (facilities, levels);
    }

    function addFacility(
        string calldata name,
        uint256 cons,
        uint256 str,
        uint256 agi,
        uint256 intel
    ) public onlyOwner {
        facilities.push(Facility(facilityCounter, name, cons, str, agi, intel));
        facilityCounter++;
    }

    modifier onlyHeroOwner(uint256 _heroId) {
        require(msg.sender == heroToken.ownerOf(_heroId), "not hero owner");
        _;
    }
    modifier hasAccount() {
        require(heroToken.isSignedUp() == true, "no account");
        _;
    }

    function _checkBalance(
        address addr,
        uint256 tokenId,
        uint256 requiredAmount
    ) internal view returns (bool) {
        uint256 balanceAtAddr = tokens.balanceOf(addr, tokenId);
        if (balanceAtAddr < requiredAmount) {
            return false;
        }
        return true;
    }

    function upgradeFacility(uint32 id) public hasAccount readyToUpgrade {
        require(
            tokens.isApprovedForAll(msg.sender, address(this)),
            "You must approve first"
        );
        uint8 currentLevel = addressToFacilityLevel[msg.sender][id];
        require(currentLevel < 255, "Facility is already max level");

        require(
            _checkBalance(
                msg.sender,
                currencyTokenId,
                currentLevel + 1 * basePrice
            ),
            "insufficient tokens"
        );

        if (currentLevel == 0) {
            addressToFacilitiesCount[msg.sender] =
                addressToFacilitiesCount[msg.sender] +
                1;
        }
        tokens.safeTransferFrom(
            msg.sender,
            address(this),
            currencyTokenId,
            (currentLevel + 1) * basePrice,
            ""
        );
        addressUpgradeCooldown[msg.sender] = uint32(
            block.timestamp + (currentLevel + 1) * baseUpgradeCooldown
        );
        addressToFacilityLevel[msg.sender][id] = currentLevel + 1;
    }

    function trainAtFacility(uint256 _heroId, uint32 _facilityId)
        external
        hasAccount
        onlyHeroOwner(_heroId)
    {
        require(
            heroIsReadyToTrainAt[_heroId] < block.timestamp,
            "Hero is not ready to train yet.."
        );
        uint8 facilityLevel = addressToFacilityLevel[msg.sender][_facilityId];
        require(facilityLevel > 0, "You haven't created this facility yet");
        Facility storage facility = facilities[_facilityId];
        heroIsReadyToTrainAt[_heroId] = uint32(
            block.timestamp + trainingCooldown
        );
        heroToken.skillUp(
            _heroId,
            facility.consGain * facilityLevel,
            facility.strGain * facilityLevel,
            facility.agiGain * facilityLevel,
            facility.intGain * facilityLevel
        );
    }

    function onERC1155Received(
        address,
        address,
        uint256 tokenId,
        uint256 amount,
        bytes memory data
    ) public virtual override returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }
}
